import axios from 'axios';
import { Tenant, CreateTenantRequest, UpdateTenantRequest } from '../types/tenant';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'https://greenpm-dev-backend-working-yvfzslnr7a-uc.a.run.app';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('authToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const tenantService = {
  // Get all tenants for the current landlord
  async getTenants(): Promise<Tenant[]> {
    const response = await api.get('/api/v1/tenants');
    return response.data;
  },

  // Get a single tenant by ID
  async getTenant(id: string): Promise<Tenant> {
    const response = await api.get(`/api/v1/tenants/${id}`);
    return response.data;
  },

  // Create a new tenant
  async createTenant(tenant: CreateTenantRequest): Promise<Tenant> {
    const response = await api.post('/api/v1/tenants', tenant);
    return response.data;
  },

  // Update an existing tenant
  async updateTenant(tenant: UpdateTenantRequest): Promise<Tenant> {
    const response = await api.put(`/api/v1/tenants/${tenant.id}`, tenant);
    return response.data;
  },

  // Delete a tenant
  async deleteTenant(id: string): Promise<void> {
    await api.delete(`/api/v1/tenants/${id}`);
  },

  // Upload tenant documents
  async uploadDocuments(tenantId: string, files: File[]): Promise<string[]> {
    const formData = new FormData();
    files.forEach((file) => {
      formData.append('documents', file);
    });

    const response = await api.post(`/api/v1/tenants/${tenantId}/documents`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data.documentUrls;
  },

  // Delete tenant document
  async deleteDocument(tenantId: string, documentUrl: string): Promise<void> {
    await api.delete(`/api/v1/tenants/${tenantId}/documents`, {
      data: { documentUrl },
    });
  },

  // Get tenants by property
  async getTenantsByProperty(propertyId: string): Promise<Tenant[]> {
    const response = await api.get(`/api/v1/properties/${propertyId}/tenants`);
    return response.data;
  },
};
