import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { DashboardService } from '../services/api';
import { 
  BuildingOfficeIcon, 
  DocumentTextIcon, 
  ClipboardDocumentListIcon,
  WrenchScrewdriverIcon,
  CreditCardIcon,
  ExclamationTriangleIcon
} from '@heroicons/react/24/outline';

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const data = await DashboardService.getDashboardStats();
      setStats(data);
    } catch (err: any) {
      setError(err.message || 'Failed to fetch dashboard data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <ExclamationTriangleIcon className="h-5 w-5 text-red-400" />
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">Error</h3>
            <p className="text-sm text-red-700 mt-1">{error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="bg-white overflow-hidden shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h1 className="text-2xl font-bold text-gray-900">
            Welcome back, {user?.first_name}!
          </h1>
          <p className="mt-1 text-sm text-gray-600">
            Here's what's happening with your {user?.role === 'tenant' ? 'rental' : 'properties'} today.
          </p>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        {user?.role === 'landlord' && (
          <>
            <StatCard
              title="Total Properties"
              value={stats?.total_properties || 0}
              icon={BuildingOfficeIcon}
              color="bg-blue-500"
            />
            <StatCard
              title="Active Leases"
              value={stats?.active_leases || 0}
              icon={ClipboardDocumentListIcon}
              color="bg-green-500"
            />
            <StatCard
              title="Pending Applications"
              value={stats?.pending_applications || 0}
              icon={DocumentTextIcon}
              color="bg-yellow-500"
            />
            <StatCard
              title="Open Maintenance"
              value={stats?.open_maintenance || 0}
              icon={WrenchScrewdriverIcon}
              color="bg-red-500"
            />
          </>
        )}

        {user?.role === 'tenant' && (
          <>
            <StatCard
              title="Active Applications"
              value={stats?.active_applications || 0}
              icon={DocumentTextIcon}
              color="bg-blue-500"
            />
            <StatCard
              title="Current Lease"
              value={stats?.current_lease ? 1 : 0}
              icon={ClipboardDocumentListIcon}
              color="bg-green-500"
            />
            <StatCard
              title="Maintenance Requests"
              value={stats?.maintenance_requests || 0}
              icon={WrenchScrewdriverIcon}
              color="bg-yellow-500"
            />
            <StatCard
              title="Pending Payments"
              value={stats?.pending_payments || 0}
              icon={CreditCardIcon}
              color="bg-red-500"
            />
          </>
        )}

        {user?.role === 'admin' && (
          <>
            <StatCard
              title="Total Users"
              value={stats?.total_users || 0}
              icon={BuildingOfficeIcon}
              color="bg-blue-500"
            />
            <StatCard
              title="Total Properties"
              value={stats?.total_properties || 0}
              icon={BuildingOfficeIcon}
              color="bg-green-500"
            />
            <StatCard
              title="Active Leases"
              value={stats?.active_leases || 0}
              icon={ClipboardDocumentListIcon}
              color="bg-yellow-500"
            />
            <StatCard
              title="Open Maintenance"
              value={stats?.open_maintenance || 0}
